    font-weight: 600;
    cursor: pointer;
    transition: var(--transition-smooth);
}

.continue-shopping:hover {
    background: #b8941f;
    transform: translateY(-2px);
}

/* Footer */
.footer {
    background: var(--charcoal);
    color: white;
    margin-top: 4rem;
}

.footer-content {
    max-width: 1200px;
    margin: 0 auto;
    padding: 3rem 2rem;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
}

.footer-section h3,
.footer-section h4 {
    margin-bottom: 1rem;
    color: var(--soft-gold);
}

.footer-section ul {
    list-style: none;
}

.footer-section ul li {
    margin-bottom: 0.5rem;
}

.footer-section a {
    color: #ccc;
    text-decoration: none;
    transition: var(--transition-fast);
}

.footer-section a:hover {
    color: var(--soft-gold);
}

.footer-bottom {
    text-align: center;
    padding: 1rem;
    border-top: 1px solid #444;
    color: #ccc;
}

/* Responsive Design */
@media (max-width: 768px) {
    .nav-menu {
        position: fixed;
        left: -100%;
        top: 80px;
