        grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
        gap: 1.5rem;
    }

    .category-btn {
        padding: 1.5rem 1rem;
    }

    .category-icon {
        font-size: 2rem;
    }

    .category-name {
        font-size: 1.1rem;
    }

    .product-detail-content {
        grid-template-columns: 1fr;
