    }
}

/* Loading Animation */
.loading {
    display: inline-block;
    width: 20px;
    height: 20px;
    border: 3px solid var(--beige);
    border-radius: 50%;
    border-top-color: var(--soft-gold);
    animation: spin 1s ease-in-out infinite;
}

@keyframes spin {
    to { transform: rotate(360deg); }
}

/* Smooth Animations */
.fade-in {
    animation: fadeIn 0.6s ease-in-out;
}

.slide-in-right {
    animation: slideInRight 0.5s ease-out;
}

@keyframes slideInRight {
    from {
        transform: translateX(100%);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

.bounce-in {
    animation: bounceIn 0.6s ease-out;
}

@keyframes bounceIn {
    0% {
        transform: scale(0.3);
        opacity: 0;
    }
    50% {
        transform: scale(1.05);
    }
    70% {
        transform: scale(0.9);
    }
    100% {
        transform: scale(1);
        opacity: 1;
    }
}

/* Utility Classes */
.text-center { text-align: center; }
.text-gold { color: var(--soft-gold); }
.bg-pink { background-color: var(--soft-pink); }
.hidden { display: none; }
.visible { display: block; }

/* Footer Styles */
.footer {
    background: linear-gradient(135deg, var(--ivory), var(--beige));
    margin-top: 4rem;
    padding: 3rem 0 1rem;
    border-top: 1px solid var(--blush-pink);
}

.footer-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 2rem;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    text-align: center;
}

.footer-section h3 {
    font-family: var(--font-serif);
    font-size: 1.2rem;
    font-weight: 600;
    color: var(--text-dark);
    margin-bottom: 1rem;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.footer-links {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
    gap: 0.5rem;
}

.footer-link {
    text-decoration: none;
    color: var(--text-light);
    font-weight: 500;
    transition: var(--transition-fast);
    position: relative;
    padding: 0.25rem 0;
}

.footer-link:hover {
    color: var(--soft-gold);
    text-decoration: underline;
    text-decoration-color: var(--soft-gold);
    text-underline-offset: 3px;
}

.footer-divider {
    color: var(--text-light);
    margin: 0 0.5rem;
    opacity: 0.6;
}

.footer-connect {
    display: flex;
    flex-direction: column;
    gap: 1rem;
    align-items: center;
}

.footer-phone {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    font-weight: 600;
    color: var(--text-dark);
    font-size: 1.1rem;
}

.phone-icon {
    font-size: 1.2rem;
}

.footer-social {
    display: flex;
    align-items: center;
    gap: 0.5rem;
}

.footer-social-link {
    text-decoration: none;
    color: var(--text-light);
    font-weight: 500;
    transition: var(--transition-fast);
    padding: 0.5rem 1rem;
    border-radius: 20px;
    background: rgba(255, 255, 255, 0.5);
}

.footer-social-link:hover {
    color: white;
    background: var(--soft-gold);
    transform: translateY(-2px);
    text-decoration: none;
}

.footer-copyright {
    border-top: 1px solid var(--blush-pink);
    margin-top: 2rem;
    padding: 1.5rem 0;
    text-align: center;
    background: rgba(255, 255, 255, 0.3);
}

.footer-copyright p {
    color: var(--text-light);
    font-size: 0.9rem;
    margin: 0;
}

