    margin: 0 auto;
    padding: 2rem;
    min-height: 60vh;
}

.wishlist-header {
    text-align: center;
    padding: 3rem 2rem;
    background: linear-gradient(135deg, var(--soft-pink), var(--beige));
    border-radius: 20px;
    margin-bottom: 3rem;
}

.wishlist-header h1 {
    font-size: 3rem;
    margin-bottom: 1rem;
    color: var(--text-dark);
    font-family: var(--font-serif);
}

.wishlist-header p {
    font-size: 1.2rem;
    color: var(--text-light);
}

.wishlist-items {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2rem;
    margin-bottom: 2rem;
}

.wishlist-item {
    background: white;
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 4px 20px rgba(0,0,0,0.08);
    transition: var(--transition-smooth);
    position: relative;
}

.wishlist-item:hover {
    transform: translateY(-5px);
    box-shadow: 0 8px 30px rgba(0,0,0,0.12);
}

.wishlist-item-image {
    width: 100%;
    height: 200px;
    background: var(--beige);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 3rem;
    color: var(--text-light);
    position: relative;
}

.wishlist-item-info {
    padding: 1.5rem;
}

.wishlist-item-name {
    font-size: 1.1rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
    color: var(--text-dark);
}

.wishlist-item-price {
    font-size: 1.2rem;
    font-weight: 700;
    color: var(--soft-gold);
    margin-bottom: 1rem;
}

.wishlist-item-actions {
    display: flex;
    gap: 0.5rem;
}

.add-to-cart-from-wishlist {
    flex: 1;
    padding: 0.75rem;
    background: var(--blush-pink);
    border: none;
    border-radius: 8px;
    color: var(--text-dark);
    font-weight: 600;
    cursor: pointer;
    transition: var(--transition-fast);
}

.add-to-cart-from-wishlist:hover {
    background: var(--soft-gold);
    color: white;
    transform: translateY(-2px);
}

.remove-from-wishlist {
    padding: 0.75rem;
    background: none;
    border: 1px solid var(--text-light);
    border-radius: 8px;
    color: var(--text-light);
    font-weight: 600;
    cursor: pointer;
    transition: var(--transition-fast);
    min-width: 40px;
}

.remove-from-wishlist:hover {
    background: #e74c3c;
    border-color: #e74c3c;
    color: white;
}

/* Empty Wishlist State */
.wishlist-empty {
    text-align: center;
    padding: 4rem 2rem;
    background: white;
    border-radius: 20px;
    box-shadow: 0 4px 20px rgba(0,0,0,0.05);
}

.empty-heart {
    font-size: 4rem;
    color: var(--text-light);
    margin-bottom: 1rem;
    display: block;
}

.wishlist-empty h3 {
    font-size: 1.5rem;
    margin-bottom: 1rem;
    color: var(--text-dark);
    font-family: var(--font-serif);
}

.wishlist-empty p {
    font-size: 1.1rem;
    color: var(--text-light);
    margin-bottom: 2rem;
}

/* Responsive Design for Wishlist */
@media (max-width: 768px) {
    .wishlist-content {
        padding: 1rem;
    }

    .wishlist-header {
        padding: 2rem 1rem;
        margin-bottom: 2rem;
    }

    .wishlist-header h1 {
        font-size: 2.5rem;
    }

    .wishlist-items {
        grid-template-columns: 1fr;
        gap: 1.5rem;
    }
}

