/* Reset and Base Styles */
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    /* Premium Color Palette */
    --blush-pink: #f4d2d7;
    --soft-pink: #f8e8ea;
    --beige: #f5f1eb;
    --ivory: #f8f6f2;
    --soft-gold: #d4af37;
    --light-gold: #f4e9a1;
    --charcoal: #2c2c2c;
    --soft-black: #1a1a1a;
    --text-dark: #333333;
    --text-light: #666666;
    --white: #ffffff;

    /* Typography */
    --font-serif: 'Playfair Display', serif;
    --font-sans: 'Inter', sans-serif;

    /* Transitions */
    --transition-smooth: all 0.3s cubic-bezier(0.4, 0, 0.2, 1);
    --transition-fast: all 0.2s ease;
}

html {
    scroll-behavior: smooth;
}

body {
    font-family: var(--font-sans);
    line-height: 1.6;
    color: var(--text-dark);
    background: linear-gradient(135deg, #f8f6f2 0%, #f4f1eb 50%, #f0ede5 100%);
    min-height: 100vh;
    overflow-x: hidden;
}

/* Typography */
h1, h2, h3, h4 {
    font-family: var(--font-serif);
    font-weight: 600;
    line-height: 1.2;
}

h1 { font-size: 2.5rem; }
h2 { font-size: 2rem; }
h3 { font-size: 1.5rem; }
h4 { font-size: 1.25rem; }

/* Navigation */
.navbar {
    position: fixed;
    top: 0;
    width: 100%;
    background: rgba(255, 255, 255, 0.95);
    backdrop-filter: blur(10px);
    border-bottom: 1px solid var(--blush-pink);
    z-index: 1000;
    transition: var(--transition-smooth);
}

.nav-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 1rem 2rem;
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.nav-logo h2 {
    color: var(--soft-gold);
    font-size: 1.8rem;
    font-weight: 700;
}

.nav-menu {
    display: flex;
    list-style: none;
    gap: 2rem;
}

.nav-link {
    text-decoration: none;
    color: var(--text-dark);
    font-weight: 500;
    position: relative;
    transition: var(--transition-fast);
}

.nav-link:hover,
.nav-link.active {
    color: var(--soft-gold);
}

.nav-link::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: var(--soft-gold);
    transition: var(--transition-fast);
}

.nav-link:hover::after,
.nav-link.active::after {
    width: 100%;
}

.nav-icons {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.cart-icon {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    cursor: pointer;
    padding: 0.5rem 1rem;
    background: var(--blush-pink);
    border-radius: 25px;
    transition: var(--transition-fast);
}

.cart-icon:hover {
    background: var(--soft-pink);
    transform: translateY(-2px);
}

.cart-count {
    background: var(--soft-gold);
    color: white;
    border-radius: 50%;
    width: 20px;
    height: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 0.8rem;
    font-weight: 600;
}

.hamburger {
    display: none;
    flex-direction: column;
    cursor: pointer;
}

.hamburger .bar {
    width: 25px;
    height: 3px;
    background: var(--text-dark);
    margin: 3px 0;
    transition: var(--transition-fast);
}

/* Cart Sidebar */
.cart-sidebar {
    position: fixed;
    top: 0;
    right: -400px;
    width: 400px;
    height: 100vh;
    background: white;
    box-shadow: -5px 0 15px rgba(0,0,0,0.1);
    z-index: 1001;
    transition: var(--transition-smooth);
    display: flex;
    flex-direction: column;
}

.cart-sidebar.open {
    right: 0;
}

.cart-header {
    padding: 2rem;
    border-bottom: 1px solid var(--beige);
    display: flex;
    justify-content: space-between;
    align-items: center;
}

.close-cart {
    font-size: 2rem;
    cursor: pointer;
    color: var(--text-light);
    transition: var(--transition-fast);
}

.close-cart:hover {
    color: var(--text-dark);
}

.cart-items {
    flex: 1;
    padding: 1rem;
    overflow-y: auto;
}

.cart-item {
    display: flex;
    align-items: center;
    gap: 1rem;
    padding: 1rem;
    border-bottom: 1px solid var(--beige);
}

.cart-item-image {
    width: 60px;
    height: 60px;
    background: var(--beige);
    border-radius: 8px;
    object-fit: cover;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
}

.cart-item-details {
    flex: 1;
}

.cart-item-name {
    font-weight: 600;
    margin-bottom: 0.25rem;
}

.cart-item-price {
    color: var(--soft-gold);
    font-weight: 600;
}

.cart-item-controls {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    margin-top: 0.5rem;
}

.quantity-btn {
    width: 30px;
    height: 30px;
    border: 1px solid var(--blush-pink);
    background: white;
    border-radius: 50%;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    transition: var(--transition-fast);
}

.quantity-btn:hover {
    background: var(--blush-pink);
}

.remove-btn {
    background: none;
    border: none;
    color: var(--text-light);
    cursor: pointer;
    font-size: 0.9rem;
    margin-left: 0.5rem;
    transition: var(--transition-fast);
}

.remove-btn:hover {
    color: #e74c3c;
}

.cart-footer {
    padding: 2rem;
    border-top: 1px solid var(--beige);
    background: var(--soft-pink);
}

.cart-total {
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 1rem;
    text-align: center;
}

.checkout-btn {
    width: 100%;
    padding: 1rem;
    background: var(--soft-gold);
    color: white;
    border: none;
    border-radius: 8px;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
    transition: var(--transition-fast);
}

.checkout-btn:hover {
    background: #b8941f;
    transform: translateY(-2px);
}

/* Main Content */
.main-content {
    margin-top: 80px;
    min-height: calc(100vh - 80px);
}

.tab-content {
    display: none;
    animation: fadeIn 0.5s ease-in-out;
}

.tab-content.active {
    display: block;
}

@keyframes fadeIn {
    from { opacity: 0; transform: translateY(20px); }
    to { opacity: 1; transform: translateY(0); }
}

/* Hero Carousel */
.hero-carousel {
    position: relative;
    height: 70vh;
    overflow: hidden;
    margin-bottom: 4rem;
}

.carousel-container {
    position: relative;
    height: 100%;
}

.carousel-slide {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, var(--blush-pink), var(--soft-pink));
    display: flex;
    align-items: center;
    justify-content: center;
    opacity: 0;
    transition: var(--transition-smooth);
}

.carousel-slide.active {
    opacity: 1;
}

.carousel-slide:nth-child(1) { background: linear-gradient(135deg, var(--blush-pink), var(--soft-pink)); }
.carousel-slide:nth-child(2) { background: linear-gradient(135deg, var(--beige), var(--light-gold)); }
.carousel-slide:nth-child(3) { background: linear-gradient(135deg, var(--soft-pink), var(--blush-pink)); }
.carousel-slide:nth-child(4) { background: linear-gradient(135deg, var(--light-gold), var(--beige)); }

.slide-content {
    text-align: center;
    color: var(--text-dark);
    max-width: 600px;
    padding: 2rem;
}

.slide-content h1 {
    font-size: 3.5rem;
    margin-bottom: 1rem;
    font-weight: 700;
}

.slide-content p {
    font-size: 1.2rem;
    margin-bottom: 2rem;
    color: var(--text-light);
}

.cta-btn {
    padding: 1rem 2rem;
    background: var(--soft-gold);
    color: white;
    border: none;
    border-radius: 50px;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
    transition: var(--transition-smooth);
    box-shadow: 0 4px 15px rgba(212, 175, 55, 0.3);
}

.cta-btn:hover {
    background: #b8941f;
    transform: translateY(-3px);
    box-shadow: 0 6px 20px rgba(212, 175, 55, 0.4);
}

.carousel-nav {
    position: absolute;
    top: 50%;
    transform: translateY(-50%);
    width: 100%;
    display: flex;
    justify-content: space-between;
    padding: 0 2rem;
}

.carousel-btn {
    width: 50px;
    height: 50px;
    border: 2px solid var(--soft-gold);
    background: rgba(255, 255, 255, 0.9);
    border-radius: 50%;
    cursor: pointer;
    font-size: 1.2rem;
    color: var(--soft-gold);
    transition: var(--transition-fast);
}

.carousel-btn:hover {
    background: var(--soft-gold);
    color: white;
    transform: scale(1.1);
}

.carousel-dots {
    position: absolute;
    bottom: 2rem;
    left: 50%;
    transform: translateX(-50%);
    display: flex;
    gap: 1rem;
}

.dot {
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background: rgba(255, 255, 255, 0.5);
    cursor: pointer;
    transition: var(--transition-fast);
}

.dot.active {
    background: var(--soft-gold);
    transform: scale(1.2);
}

/* Category Section */
.category-section {
    padding: 4rem 0;
    background: linear-gradient(135deg, var(--soft-pink), var(--beige));
    margin-bottom: 3rem;
}

.category-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 2rem;
    text-align: center;
}

.category-container h2 {
    font-size: 2.5rem;
    margin-bottom: 3rem;
    color: var(--text-dark);
}

.category-buttons {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(220px, 1fr));
    gap: 2rem;
    margin-top: 2rem;
}

.category-btn {
    background: white;
    border: none;
    border-radius: 20px;
    padding: 2rem 1.5rem;
    cursor: pointer;
    transition: var(--transition-smooth);
    box-shadow: 0 4px 20px rgba(0,0,0,0.08);
    text-align: center;
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 0.5rem;
}

.category-btn:hover {
    transform: translateY(-8px);
    box-shadow: 0 8px 30px rgba(212, 175, 55, 0.2);
    background: var(--light-gold);
}

.category-icon {
    font-size: 2.5rem;
    margin-bottom: 0.5rem;
}

.category-name {
    font-size: 1.2rem;
    font-weight: 600;
    color: var(--text-dark);
    font-family: var(--font-serif);
}

.category-desc {
    font-size: 0.9rem;
    color: var(--text-light);
    font-weight: 400;
}

/* Product Sections */
.product-sections {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 2rem;
}

.section-header {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin: 3rem 0 1.5rem;
}

.section-header h2 {
    color: var(--text-dark);
}

.view-all-btn {
    padding: 0.75rem 1.5rem;
    background: transparent;
    border: 2px solid var(--soft-gold);
    color: var(--soft-gold);
    border-radius: 25px;
    font-weight: 600;
    cursor: pointer;
    transition: var(--transition-fast);
}

.view-all-btn:hover {
    background: var(--soft-gold);
    color: white;
    transform: translateY(-2px);
}

.product-scroll {
    display: flex;
    gap: 1.5rem;
    overflow-x: auto;
    padding: 1rem 0;
    scroll-behavior: smooth;
    scrollbar-width: none;
    -ms-overflow-style: none;
}

.product-scroll::-webkit-scrollbar {
    display: none;
}

/* Product Cards */
.product-card {
    flex: 0 0 280px;
    background: white;
    border-radius: 15px;
    overflow: hidden;
    box-shadow: 0 4px 20px rgba(0,0,0,0.08);
    transition: var(--transition-smooth);
    cursor: pointer;
}

.product-card:hover {
    transform: translateY(-10px);
    box-shadow: 0 8px 30px rgba(0,0,0,0.12);
}

.product-image {
    width: 100%;
    height: 200px;
    background: var(--beige);
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 3rem;
    color: var(--text-light);
    position: relative;
    overflow: hidden;
}

.product-image::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: linear-gradient(45deg, var(--blush-pink), transparent, var(--light-gold));
    opacity: 0;
    transition: var(--transition-fast);
}

.product-card:hover .product-image::before {
    opacity: 0.3;
}

.product-info {
    padding: 1.5rem;
}

.product-name {
    font-size: 1.1rem;
    font-weight: 600;
    margin-bottom: 0.5rem;
    color: var(--text-dark);
}

.product-price {
    font-size: 1.2rem;
    font-weight: 700;
    color: var(--soft-gold);
    margin-bottom: 1rem;
}

.add-to-cart {
    width: 100%;
    padding: 0.75rem;
    background: var(--blush-pink);
    border: none;
    border-radius: 8px;
    color: var(--text-dark);
    font-weight: 600;
    cursor: pointer;
    transition: var(--transition-fast);
}

.add-to-cart:hover {
    background: var(--soft-gold);
    color: white;
    transform: translateY(-2px);
}

/* Products Grid */
.tab-header {
    text-align: center;
    padding: 3rem 2rem;
    background: linear-gradient(135deg, var(--soft-pink), var(--beige));
}

.tab-header h1 {
    font-size: 3rem;
    margin-bottom: 1rem;
    color: var(--text-dark);
}

.tab-header p {
    font-size: 1.2rem;
    color: var(--text-light);
}

.products-grid {
    max-width: 1200px;
    margin: 0 auto;
    padding: 3rem 2rem;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: 2rem;
}

/* Product Detail Page */
.product-detail-container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 2rem;
}

.product-detail-content {
    display: grid;
    grid-template-columns: 1fr 1fr;
    gap: 4rem;
    align-items: start;
}

.product-detail-image {
    display: flex;
    justify-content: center;
    align-items: center;
}

.product-image-large {
    width: 100%;
    max-width: 400px;
    height: 400px;
    background: var(--beige);
    border-radius: 20px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 8rem;
    color: var(--text-light);
    box-shadow: 0 8px 30px rgba(0,0,0,0.1);
}

.product-detail-info {
    padding: 2rem 0;
}

.product-detail-header {
    margin-bottom: 2rem;
}

.product-detail-header h1 {
    font-size: 2.5rem;
    margin-bottom: 1rem;
    color: var(--text-dark);
}

.product-detail-price {
    font-size: 2rem;
    font-weight: 700;
    color: var(--soft-gold);
}

.product-detail-description {
    margin-bottom: 2rem;
}

.product-detail-description h3 {
    margin-bottom: 1rem;
    color: var(--text-dark);
}

.product-detail-description p {
    line-height: 1.8;
    color: var(--text-light);
    font-size: 1.1rem;
}

.product-detail-actions {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

.quantity-selector {
    display: flex;
    align-items: center;
    gap: 1rem;
}

.quantity-selector label {
    font-weight: 600;
    color: var(--text-dark);
}

.quantity-controls {
    display: flex;
    align-items: center;
    gap: 1rem;
    background: white;
    border: 2px solid var(--beige);
    border-radius: 10px;
    padding: 0.5rem 1rem;
}

.quantity-btn-detail {
    width: 40px;
    height: 40px;
    border: 1px solid var(--blush-pink);
    background: white;
    border-radius: 50%;
    cursor: pointer;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.2rem;
    font-weight: 600;
    color: var(--text-dark);
    transition: var(--transition-fast);
}

.quantity-btn-detail:hover {
    background: var(--blush-pink);
}

.quantity-display {
    font-size: 1.2rem;
    font-weight: 600;
    min-width: 30px;
    text-align: center;
    color: var(--text-dark);
}

.add-to-cart-detail {
    padding: 1rem 2rem;
    background: var(--soft-gold);
    color: white;
    border: none;
    border-radius: 10px;
    font-size: 1.2rem;
    font-weight: 600;
    cursor: pointer;
    transition: var(--transition-smooth);
    box-shadow: 0 4px 15px rgba(212, 175, 55, 0.3);
}

.add-to-cart-detail:hover {
    background: #b8941f;
    transform: translateY(-2px);
    box-shadow: 0 6px 20px rgba(212, 175, 55, 0.4);
}

.back-to-category {
    padding: 1rem 2rem;
    background: transparent;
    border: 2px solid var(--soft-gold);
    color: var(--soft-gold);
    border-radius: 10px;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
    transition: var(--transition-fast);
}

.back-to-category:hover {
    background: var(--soft-gold);
    color: white;
    transform: translateY(-2px);
}

/* About and Contact Pages */
.about-content,
.contact-content {
    max-width: 800px;
    margin: 0 auto;
    padding: 3rem 2rem;
}

.about-content h1,
.contact-content h1 {
    text-align: center;
    margin-bottom: 2rem;
    color: var(--text-dark);
}

.about-content p {
    font-size: 1.1rem;
    line-height: 1.8;
    margin-bottom: 1.5rem;
    color: var(--text-light);
}

.contact-info {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
    margin-top: 2rem;
}

.contact-item {
    text-align: center;
    padding: 2rem;
    background: white;
    border-radius: 15px;
    box-shadow: 0 4px 20px rgba(0,0,0,0.05);
}

.contact-item h3 {
    margin-bottom: 1rem;
    color: var(--soft-gold);
}

.social-links {
    display: flex;
    gap: 1rem;
    justify-content: center;
    margin-top: 1rem;
}

.social-link {
    padding: 0.5rem 1rem;
    background: var(--blush-pink);
    color: var(--text-dark);
    text-decoration: none;
    border-radius: 25px;
    transition: var(--transition-fast);
}

.social-link:hover {
    background: var(--soft-gold);
    color: white;
    transform: translateY(-2px);
}

/* Checkout Form */
.checkout-content {
    max-width: 600px;
    margin: 0 auto;
    padding: 3rem 2rem;
}

.checkout-form {
    background: white;
    padding: 2rem;
    border-radius: 15px;
    box-shadow: 0 4px 20px rgba(0,0,0,0.05);
}

.form-group {
    margin-bottom: 1.5rem;
}

.form-group label {
    display: block;
    margin-bottom: 0.5rem;
    font-weight: 600;
    color: var(--text-dark);
}

.form-group input,
.form-group textarea,
.form-group select {
    width: 100%;
    padding: 1rem;
    border: 2px solid var(--beige);
    border-radius: 8px;
    font-size: 1rem;
    transition: var(--transition-fast);
}

.form-group input:focus,
.form-group textarea:focus,
.form-group select:focus {
    outline: none;
    border-color: var(--soft-gold);
}

.order-summary {
    background: var(--soft-pink);
    padding: 1.5rem;
    border-radius: 10px;
    margin: 2rem 0;
}

.order-summary h3 {
    margin-bottom: 1rem;
    color: var(--text-dark);
}

.checkout-item {
    display: flex;
    justify-content: space-between;
    margin-bottom: 0.5rem;
    padding: 0.5rem 0;
    border-bottom: 1px solid var(--beige);
}

.checkout-total {
    font-weight: 700;
    font-size: 1.2rem;
    margin-top: 1rem;
    padding-top: 1rem;
    border-top: 2px solid var(--blush-pink);
}

.submit-order-btn {
    width: 100%;
    padding: 1rem;
    background: var(--soft-gold);
    color: white;
    border: none;
    border-radius: 8px;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
    transition: var(--transition-smooth);
}

.submit-order-btn:hover {
    background: #b8941f;
    transform: translateY(-2px);
}

/* Order Confirmation */
.confirmation-content {
    max-width: 600px;
    margin: 0 auto;
    padding: 3rem 2rem;
    text-align: center;
}

.confirmation-success {
    background: white;
    padding: 3rem;
    border-radius: 20px;
    box-shadow: 0 8px 30px rgba(0,0,0,0.1);
}

.confirmation-success h1 {
    color: var(--soft-gold);
    margin-bottom: 2rem;
}

.confirmation-details {
    background: var(--soft-pink);
    padding: 2rem;
    border-radius: 15px;
    margin: 2rem 0;
    text-align: left;
}

.confirmation-details h3 {
    margin-bottom: 1rem;
    color: var(--text-dark);
}

.confirmation-details ul {
    list-style: none;
    padding: 0;
}

.confirmation-details li {
    margin-bottom: 0.5rem;
    color: var(--text-light);
}

.continue-shopping {
    padding: 1rem 2rem;
    background: var(--soft-gold);
    color: white;
    border: none;
    border-radius: 25px;
    font-size: 1.1rem;
    font-weight: 600;
    cursor: pointer;
    transition: var(--transition-smooth);
}

.continue-shopping:hover {
    background: #b8941f;
    transform: translateY(-2px);
}

/* Footer */
.footer {
    background: var(--charcoal);
    color: white;
    margin-top: 4rem;
}

.footer-content {
    max-width: 1200px;
    margin: 0 auto;
    padding: 3rem 2rem;
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: 2rem;
}

.footer-section h3,
.footer-section h4 {
    margin-bottom: 1rem;
    color: var(--soft-gold);
}

.footer-section ul {
    list-style: none;
}

.footer-section ul li {
    margin-bottom: 0.5rem;
}

.footer-section a {
    color: #ccc;
    text-decoration: none;
    transition: var(--transition-fast);
}

.footer-section a:hover {
    color: var(--soft-gold);
}

.footer-bottom {
    text-align: center;
    padding: 1rem;
    border-top: 1px solid #444;
    color: #ccc;
}

/* Responsive Design */
@media (max-width: 768px) {
    .nav-menu {
        position: fixed;
        left: -100%;
        top: 80px;
        flex-direction: column;
        background-color: white;
        width: 100%;
        text-align: center;
        transition: var(--transition-smooth);
        box-shadow: 0 10px 27px rgba(0,0,0,0.05);
        padding: 2rem 0;
    }

    .nav-menu.active {
        left: 0;
    }

    .hamburger {
        display: flex;
    }

    .hamburger.active .bar:nth-child(2) {
        opacity: 0;
    }

    .hamburger.active .bar:nth-child(1) {
        transform: translateY(8px) rotate(45deg);
    }

    .hamburger.active .bar:nth-child(3) {
        transform: translateY(-8px) rotate(-45deg);
    }

    .cart-sidebar {
        width: 100%;
        right: -100%;
    }

    .slide-content h1 {
        font-size: 2.5rem;
    }

    .tab-header h1 {
        font-size: 2rem;
    }

    .product-scroll {
        gap: 1rem;
    }

    .product-card {
        flex: 0 0 250px;
    }

    .products-grid {
        grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
        gap: 1.5rem;
    }

    .section-header {
        flex-direction: column;
        gap: 1rem;
        text-align: center;
    }

    .nav-container {
        padding: 1rem;
    }

    .footer-content {
        padding: 2rem 1rem;
    }

    .category-container h2 {
        font-size: 2rem;
    }

    .category-buttons {
        grid-template-columns: repeat(auto-fit, minmax(180px, 1fr));
        gap: 1.5rem;
    }

    .category-btn {
        padding: 1.5rem 1rem;
    }

    .category-icon {
        font-size: 2rem;
    }

    .category-name {
        font-size: 1.1rem;
    }

    .product-detail-content {
        grid-template-columns: 1fr;
        gap: 2rem;
    }

    .product-image-large {
        max-width: 300px;
        height: 300px;
        font-size: 6rem;
    }
}

@media (max-width: 480px) {
    .slide-content h1 {
        font-size: 2rem;
    }

    .slide-content {
        padding: 1rem;
    }

    .product-card {
        flex: 0 0 200px;
    }

    .products-grid {
        grid-template-columns: 1fr;
    }

    .checkout-content,
    .confirmation-content,
    .about-content,
    .contact-content {
        padding: 2rem 1rem;
    }

    .product-image-large {
        max-width: 250px;
        height: 250px;
        font-size: 4rem;
    }
}

/* Loading Animation */
.loading {
    display: inline-block;
    width: 20px;
    height: 20px;
    border: 3px solid var(--beige);
    border-radius: 50%;
    border-top-color: var(--soft-gold);
    animation: spin 1s ease-in-out infinite;
}

@keyframes spin {
    to { transform: rotate(360deg); }
}

/* Smooth Animations */
.fade-in {
    animation: fadeIn 0.6s ease-in-out;
}

.slide-in-right {
    animation: slideInRight 0.5s ease-out;
}

@keyframes slideInRight {
    from {
        transform: translateX(100%);
        opacity: 0;
    }
    to {
        transform: translateX(0);
        opacity: 1;
    }
}

.bounce-in {
    animation: bounceIn 0.6s ease-out;
}

@keyframes bounceIn {
    0% {
        transform: scale(0.3);
        opacity: 0;
    }
    50% {
        transform: scale(1.05);
    }
    70% {
        transform: scale(0.9);
    }
    100% {
        transform: scale(1);
        opacity: 1;
    }
}

/* Utility Classes */
.text-center { text-align: center; }
.text-gold { color: var(--soft-gold); }
.bg-pink { background-color: var(--soft-pink); }
.hidden { display: none; }
.visible { display: block; }
